mapper.select:
SELECT CASE WHEN count(1) = 3 THEN 1 ELSE 0 END
FROM ALL_TAB_COLUMNS
WHERE OWNER = #{owner}
  AND TABLE_NAME = #{tableName}
  AND COLUMN_NAME IN ('CRT_UID', 'CRT_IP', 'CRT_DS')

SELECT CASE WHEN count(1) = 3 THEN 1 ELSE 0 END
FROM ALL_TAB_COLUMNS
WHERE OWNER = #{owner}
  AND TABLE_NAME = #{tableName}
  AND COLUMN_NAME IN ('UDT_UID', 'UDT_IP', 'UDT_DS')

mapper.update:
UPDATE ${tableName}
SET UDT_UID = #{udtUid},
    UDT_IP  = #{udtIp},
    UDT_DS  = SYSDATE
WHERE ${conditionClause}
