mapper.insert:
INSERT INTO CELL
    (
     CELL_CD
    , CELL_LEADER_UID
    , HIR_CELL_CD
    , CELL_NM
    , CELL_FEE_IDX
    , GROUP_PLACE_DIV
    , CELL_CONCEPT
    , CELL_NOTE
    , CELL_YEAR
    , CREATED_UID
    , CREATED_AT
    , CREATED_IP
    , ORD
    )
    VALUES
   (
    #{cellCd}
    , #{cellLeaderUid}
    , #{hirCellCd}
    , #{cellNm}
    , #{cellFeeIdx}
    , #{cellGroupPlace}
    , #{cellConcept}
    , #{cellNote}
    , #{cellYear}
    , #{createdUid}
    , SYSDATE()
    , #{createdIp}
    , #{ord}
   )

mapper.update:
UPDATE CELL SET
    CELL_LEADER_UID = #{cellLeaderUid}
  , HIR_CELL_CD = #{hirCellCd}
  , CELL_NM = #{cellNm}
  , CELL_FEE_IDX = #{cellFeeIdx}
  , GROUP_PLACE_DIV = #{cellGroupPlace}
  , CELL_CONCEPT = #{cellConcept}
  , CELL_NOTE = #{cellNote}
  , CELL_YEAR = #{cellYear}
  , UPDATED_UID = #{updatedUid}
  , UPDATED_AT = SYSDATE()
  , UPDATED_IP = #{updatedIp}
  , ORD = #{ord}
WHERE CELL_CD = #{cellCd}
