mapper.select:
SELECT HIR_CD , DTL_CD , DTL_CD_NM , DTL_CD_EN , DTL_CD_ABBR , DTL_CD_ORD , USE_YN , DTL_CD_RMK , CREATED_UID , UPDATED_UID FROM COM_DTL_CODE WHERE HIR_CD = #{hirCd}
AND UPPER(DTL_CD_NM) LIKE ('%' || TRIM(UPPER(#{dtlCdNm})) || '%')
AND USE_YN = #{useYn}
ORDER BY DTL_CD_ORD

mapper.update:
UPDATE COM_HIR_CODE SET
     HIR_CD_NM= #{hirCdNm},
     HIR_CD_DESC = #{hirCdDesc},
     USE_YN = #{useYn},
     HIR_CD_ORD = #{hirCdOrd}
WHERE HIR_CD = #{hirCd}

UPDATE COM_DTL_CODE SET
    DTL_CD_NM = #{dtlCdNm},
    DTL_CD_EN = #{dtlCdEn},
    DTL_CD_ABBR = #{dtlCdAbbr},
    DTL_CD_RMK = #{dtlCdRmk},
    DTL_CD_ORD = #{dtlCdOrd},
    USE_YN = #{useYn}
WHERE HIR_CD = #{hirCd}
  AND DTL_CD = #{dtlCd}
