mapper.select:
SELECT HIR_CD , DTL_CD , DTL_CD_NM , DTL_CD_EN , DTL_CD_ABBR , DTL_CD_ORD , USE_YN , DTL_CD_RMK , CREATED_UID , UPDATED_UID FROM COM_DTL_CODE WHERE HIR_CD = #{hirCd}
AND UPPER(DTL_CD_NM) CONCAT( '%' , TRIM(UPPER(#{dtlCdNm})), '%')
AND USE_YN = #{useYn}
ORDER BY DTL_CD_ORD

mapper.insert:

     SELECT CONCAT(#{cdDivSu}, LPAD(IFNULL(MAX(SUBSTR(HIR_CD, -2)), 0)+1, 2, '0'))
     FROM COM_HIR_CODE WHERE CD_DIV_SU = #{cdDivSu}

 INSERT INTO COM_HIR_CODE
 (
 HIR_CD
, CD_DIV_SU
, HIR_CD_NM
, HIR_CD_ORD
, USE_YN
, HIR_CD_DESC
, CREATED_UID
, CREATED_AT
, CREATED_IP
, UPDATED_UID
, UPDATED_AT
, UPDATED_IP
 )
 VALUES(
   #{hirCd}
 , #{cdDivSu}
 , #{hirCdNm}
 , #{hirCdOrd}
 , #{useYn}
 , #{hirCdDesc}
 , #{createdUid}
 , #{createdAt}
 , #{createdIp}
 , #{updatedUid}
 , #{updatedAt}
 , #{updatedIp}
 )


    SELECT CONCAT(#{hirCd}, LPAD(IFNULL((SELECT MAX(SUBSTR(DTL_CD, -3))
    FROM COM_DTL_CODE
    WHERE HIR_CD = #{hirCd}),0)+1, 3, '0'))
    FROM DUAL

INSERT INTO COM_DTL_CODE
(
HIR_CD
,DTL_CD
,DTL_CD_NM
,DTL_CD_EN
,DTL_CD_ABBR
,DTL_CD_ORD
,USE_YN
,DTL_CD_RMK
,CREATED_UID
,CREATED_AT
,CREATED_IP
,UPDATED_UID
,UPDATED_AT
,UPDATED_IP
)
VALUES
(
 #{hirCd}
,#{dtlCd}
,#{dtlCdNm}
,#{dtlCdEn}
,#{dtlCdAbbr}
,#{dtlCdOrd}
,#{useYn}
,#{dtlCdRmk}
,#{createdUid}
,#{createdAt}
,#{createdIp}
,#{updatedUid}
,#{updatedAt}
,#{updatedIp}
)
