mapper.update:
UPDATE MENU
SET HIR_MENU_CD = #{hirMenuCd}
  , MENU_URL    = #{menuUrl}
  , MENU_NM     = #{menuCd}
  , MENU_ORD    = #{menuOrd}
  , MENU_DESC   = #{menuDesc}
  , USE_YN      = #{useYn}
WHERE MENU_CD = #{menuCd}

mapper.select:
SELECT COUNT(*)
FROM MENU
WHERE MENU_CD = #{menuCD}

SELECT COUNT(*)
FROM MENU
WHERE MENU_NM = #{menuNM}

SELECT COUNT(*)
FROM MENU
WHERE MENU_URL = #{menuURL}
